    public JCheckBox getCaps()
    {
        //fetches the state of the checkbox
        return caps;
    }

    public boolean getChars()
    {
        //fetches the state of the checkbox
        boolean temp = false;
        if (chars.isSelected())
        {
            temp = true;
        }
        return temp;
    }

    public JSlider getLength()
    {
        //gets the max lenght of the password required 
        return length;
    }

    public JCheckBox getNum()
    {
        //fetches the state of the checkbox
        return num;
    }

    // calls the method which adds the atributes into a new entry into the tblEntries table parsing the fields as parameters
    private void createBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_createBtnActionPerformed

        programmeFunctions obj = new programmeFunctions();
        obj.createEntry(sitenameField.getText(), usernameField.getText(), passwordField.getText());
        this.dispose();


    }//GEN-LAST:event_createBtnActionPerformed

