    //signs the user up and logs them into their dashboard by:
    // - performing a blanck check with respective errors
    // - verifying the login details by : making sure it doesnt already exist
    //                                    the passwords match
                                          
    // - writing the users temporary data into text files by calling the respective methods
    // - setting the header label in the dashboard to the users email
    private void signupbtnnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_signupbtnnActionPerformed
        userFunctions obj = new userFunctions();
        arrUsers arrUserObj = new arrUsers();
        clearErrors();
        String email, mpassword, cpassword;
        email = emailadd.getText();
        mpassword = masterpassword.getText();
        cpassword = confirmpassword.getText();

        if (obj.isFieldBlank(email, mpassword, cpassword))
        {
            mainErrLbl.setText("FIELDS CANNOT BE BLANK");
        }
        if (obj.doesUsernameExist(email))
        {
            emailErrLbl.setText("EMAIL IS ALREADY REGISTERED");
        }
        if (obj.doPasswordsMatch(mpassword, cpassword) == false)
        {
            cpassErrLbl.setText("PASSWORDS DONT MATCH");
        }
        if (obj.verifyDetails(email, mpassword, cpassword))
        {
            Home homeObj = new Home();
            homeObj.setVisible(true);
            this.dispose();
            obj.createUser(email, mpassword);
            obj.signPos(emailadd.getText());
            obj.signUserIn(emailadd.getText());
            arrUserObj.setUserPos(arrUserObj.SearchUsers(emailadd.getText()));
            homeObj.usernamelbl.setText(arrUserObj.arrUsers[obj.getUserPosition()].getEmail());
        }
    }//GEN-LAST:event_signupbtnnActionPerformed

