    //determines and classifies the strenght of the password *refer to algorithm
    private void testPasswordStrengthBtnActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_testPasswordStrengthBtnActionPerformed
    {//GEN-HEADEREND:event_testPasswordStrengthBtnActionPerformed
        programmeFunctions obj = new programmeFunctions();
        //clears labels when from preious states
        vstrongLbl.setForeground(Color.BLACK);
        strongLbl.setForeground(Color.BLACK);
        okayLbl.setForeground(Color.BLACK);
        weakLbl.setForeground(Color.BLACK);
        vweakLbl.setForeground(Color.BLACK);

        /*swtich statement which highlights the corresponding jlabel, 
        in order to indicate the passwords strenght*/
        switch (obj.calcStrength(masterpassword.getText()))
        {
            case 5:
                // code block
                vstrongLbl.setForeground(Color.WHITE);
                break;
            case 4:
                // code block
                strongLbl.setForeground(Color.WHITE);
                break;
            case 3:
                // code block
                okayLbl.setForeground(Color.WHITE);
                break;
            case 2:
                // code block
                weakLbl.setForeground(Color.WHITE);
                break;
            case 1:
                // code block
                vweakLbl.setForeground(Color.WHITE);
                break;
            default:
            // code block
        }

    }//GEN-LAST:event_testPasswordStrengthBtnActionPerformed

